    NSFileManager *manager = [NSFileManager defaultManager] ;
    if (![manager fileExistsAtPath:documentPath]) {
        NSLog(@"没有文件");
    }else {
        NSEnumerator *childFilesEnumerator = [[manager subpathsAtPath:documentPath  ] objectEnumerator];
        NSString *fileName = nil;
